import { useState } from 'react';
import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';

function App() {
  const [cartOpen, setCartOpen] = useState(false);

  return (
    <>
      {cartOpen && <Cart />}
      <Header />
      <Meals />
    </>
  );
}

export default App;
